package com.nxt.net.baseapp.mvp.contract;import com.jess.arms.mvp.IPresenter;import com.jess.arms.mvp.IView;import com.jess.arms.mvp.IModel;import com.nxt.net.baseapp.contracts.XContract;/** * @author Maven Jan */public interface LoginContract {    interface Presenter extends IPresenter{        /**         * 检查用户名是否符合标准         * @param username 用户名         */        void checkUsername(String username);        /**         * 检查用户名是否符合可用（服务端）         * @param username 用户名         */        void checkUsernameUseAble(String username);        /**         * 检查密码是否正确         * @param password 密码         */        void checkPassword(String password);        /**         * 登录请求         * @param name 用户名         * @param password 密码         */        void requestLogin(String name, String password);    }    /**     * 对于经常使用的关于UI的方法可以定义到IView中,如显示隐藏进度条,和显示文字消息     */    interface View extends IView {        /**         * 用户名是否可用监测结果         * @param err 返回的msg         */        void nameFormatError(String err);        void passwordFormatError(String err);        void callLoginSuccess();        void callLoginFail(String failMassage);    }    /**     * Model层定义接口,外部只需关心Model返回的数据,无需关心内部细节,即是否使用缓存     */    interface Model extends IModel {    }}