package com.cauc.mavenj.widget;import android.content.Context;import android.util.AttributeSet;import android.widget.TextView;import com.cauc.mavenj.app.MyApplication;/** * @author Jan Maven * @date 2017/10/23 14:50 * @email cyjiang_11@163.com */public class TextViewNew extends TextView {    public TextViewNew(Context context) {        this(context, null);    }    public TextViewNew(Context context, AttributeSet attrs) {        this(context, attrs, 0);    }    public TextViewNew(Context context, AttributeSet attrs, int defStyleAttr) {        super(context, attrs, defStyleAttr);        setTypeface(MyApplication.getExtraFont());    }}